                <div id="failed_classes"></div>
            </div>
            <div class="dilosi-pr-div">
                    <div onClick={GetCheckboxValue} class="dilosi-pr-div-b">ΠΡΟΣΩΡΙΝΗ ΑΠΟΘΗΚΕΥΣΗ</div>
            </div>
