import React from "react";
import { useEffect, useState } from 'react';
import { db } from '../components/firebase.js';
import { doc, setDoc, updateDoc, arrayUnion, getDoc, getDocs, collection } from 'firebase/firestore'
import { Link } from "react-router-dom";
import "./../css/HomePage.css";
import "./../css/Student_dilosi.css";
import Nav2 from "./../components/Nav2.js"
import Footer from "./../components/footer.js"
import Menu from "./../components/student_menu.js"
import firebase from 'firebase/compat/app';

const CourseTable = () => {
    const [courses, setCourses] = useState([]);
    useEffect(() => {
        // Fetch data from Firebase when the component mounts
        const fetchCourses = async () => {
            try {
                const snapshot = await firebase.database().ref('courses').once('value');
                const data = snapshot.val();

                // Check if there's data before updating the state
                if (data) {
                    const coursesArray = Object.values(data);
                    setCourses(coursesArray);
                }
            }
            catch (error) {
                console.error('Error fetching data from Firebase:', error);
            }
        };
        fetchCourses();
    }, []); // Empty dependency array means this effect runs once when the component mounts



    return (
        <div>
            <Nav2 />
            <Menu />
            <div>
                <h2>Course Table</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Course Name</th>
                            <th>Instructor</th>
                            {/* Add more table headers as needed */}
                        </tr>
                    </thead>
                    <tbody>
                        {courses.map((course) => (
                            <tr key={course.id}>
                                <td>{course.name}</td>
                                <td>{course.instructor}</td>
                                {/* Add more table cells as needed */}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <Footer />
        </div>
    );
}

export default CourseTable;
