import React from "react";
import { useEffect, useState } from 'react';
import { db } from '../components/firebase.js';
import { doc, setDoc, updateDoc, arrayUnion, getDoc, getDocs, collection } from 'firebase/firestore'
import { Link } from "react-router-dom";
import "./../css/HomePage.css";
import "./../css/Student_dilosi.css";
import Nav2 from "./../components/Nav2.js"
import Footer from "./../components/footer.js"
import Menu from "./../components/student_menu.js"

export default function Student_dilosi() {
    const results = [];
    async function getAllCoursesFunc(e) {
        e.preventDefault()

        //"Bring me, from the collection 'courses' the document with name/value 'all_courses'"
        var database = db.database().ref().child('courses');
        database.once('value', function (snapshot) {
            if (snapshot.exists()) {
                var content = '';
                snapshot.forEach(function (data) {
                    var Title = data.val().name;
                    content += '<tr>';
                    content += '<td>' + Title + '</td>';
                    content += '</tr>';
                });
                document.getElementById("ex-table");
                $('#ex-table').append(content);
            }
        });
    }
    useEffect(() => {
        // Every time you try to enter this page check if you have a saved key at the local storage. 
        // If not, then do not allow user to enter this page and redirect to login page
        if (localStorage.getItem('role') !== "student") {
            window.location.href = '/'
        }
    }, [])

    return (
        <div>
            <Nav2 />
            <Menu />
            <div>
                <table id="ex-table">
                    <thead class="thead-inverse">
                        <tr>
                            <th>Task Title</th>
                            <th>Category</th>
                            <th>Date</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr id="tr">
                            <td id="titleTask"></td>
                            <td id="categoryTask"></td>
                            <td id="dateTask"></td>
                            <td id="statusTask"></td>
                        </tr>
                    </tbody>
                </table>
            </div>
            <Footer />
        </div>
    );
}
