import React from "react";
import { useState, useEffect } from 'react';
import { db } from '../components/firebase.js';
import { Link } from "react-router-dom";
import "./../css/HomePage.css";
import "./../css/Student_dilosi.css";
import Nav2 from "./../components/Nav2.js"
import Footer from "./../components/footer.js"
import Menu from "./../components/student_menu.js"


export default function Proswrinh1() {
    useEffect(() => {
        // Every time you try to enter this page check if you have a saved key at the local storage. 
        // If not, then do not allow user to enter this page and redirect to login page
        if (localStorage.getItem('role') !== "student") {
            window.location.href = '/'
        }
    }, []);

    return (
        <div>
            <Nav2 />
            <Menu />
            <div class="d-div1">
                <div class="div-table" id="dyn-table"></div>


                <div className="dilosi_rectangle1">
                    <div className="dilosi_div">Οριστική Υποβολή</div>
                </div>
                <h4 id="result"></h4>
            </div>
            <button onClick={generateTable(sessionStorage.getItem("res"))}> generate table</button>
            <Footer />

            <script> {

                function generateTable(data) {
                    let table = '<table class="d-table2">';
                    table += '<tr><th class="dcell">Όνομα Μαθήματος</th><th class="dcell">Κατεύθυνση</th><th class="dcell">Εξάμηνο</th> <th class="dcell">Κατηγορία</th></tr>';

                    for (var i = 0; i < data.length; i++) {
                        table += `<tr><td>${data[i]}</td></tr>`;
                    }

                    table += '</table>';
                    var gib = document.getElementById('dyn-table');
                    gib.innerHTML = table;
                }}
            </script>
        </div>


    );


}
